const List = () => (
  <ul>
    {data.map(item => (
      <div key={item.id} id='songs'>
        <div>{item.id}</div>
        <audio id={item.id} controls src={item.src}></audio>
        <div id="canvas-small">
				<AudioSpectrum
				  id={item.canvas}
				  height={100}
				  width={window.innerWidth * 0.25}
				  audioId={item.id}
				  capColor={CSS_COLOR_NAMES[Math.floor(Math.random() * 141)]}
				  capHeight={1}
				  meterWidth={1}
				  meterCount={512}
				  meterColor={[
				    {stop: 0, color: CSS_COLOR_NAMES[Math.floor(Math.random() * 141)]},
				    {stop: 0.5, color: CSS_COLOR_NAMES[Math.floor(Math.random() * 141)]},
				    {stop: 1, color: CSS_COLOR_NAMES[Math.floor(Math.random() * 141)]}
				  ]}
				  gap={4}
				/>
				</div>
      </div>
    ))}
  </ul>
);


export default function Tracks(props) {
	return (
		<div>
			<div className='title'>
				<h2>Tracks</h2>
			</div>
			<List />
		</div>
		)
}
